mapper.select:
SELECT COUNT(ID)
FROM COMMUNITY_COLLECTION
WHERE COMMUNITY_ID = #{communityId} AND USER_ID = #{userId}

SELECT DELETED
FROM COMMUNITY_COLLECTION
WHERE COMMUNITY_ID = #{communityId} AND USER_ID = #{userId}

mapper.update:
UPDATE COMMUNITY_COLLECTION
SET DELETED = #{isDeleted}
WHERE COMMUNITY_ID = #{communityId} AND USER_ID = #{userId}
